import React, { Component } from 'react'
import Loader from './loader/loader'
import Table from './table/table'
import _ from 'lodash'
import ReactPaginate from 'react-paginate';
import DetailRowView from './detailRowView/detailRowView'
import ModeSelector from './ModeSelector/ModeSelector'
import Search from './search/search'

class App extends Component {

  state = {
    isModeSelected: false,
    isLoading: false,
    data: [],
    sort: 'asc', //desc
    search: '',
    sortField: 'id',
    row: null,
    currentPage: 0,


  }

  async fetchData(url) {
    const response = await fetch(url)
    const data = await response.json();
    this.setState({
      isLoading: false,
      data: _.orderBy(data, this.state.sortField, this.state.sort)

    })

  }

  onSort = sortField => {
    const clonedData = this.state.data.concat()
    const sort = this.state.sort === 'asc' ? 'desc' : 'asc'
    const data = _.orderBy(clonedData, sortField, sort)


    this.setState({
      data,
      sort,
      sortField
    })

  }


  modeSelectHandler = url => {
    this.setState({
      isModeSelected: true,
      isLoading: true

    })

    this.fetchData(url)

  }

onRowSelect = row => {
    this.setState({ row })
  }


  handlePageClick = ({selected}) => {
    this.setState({currentPage: selected})
  }

  searchHandler = search => {
   this.setState({search, currentPage: 0}) //сбрасывает текущую страницу на первую
  }

  getFilteredData() {
    const{data,search} = this.state

    if(!search){
      return data 
    }

    return data.filter(item =>{
      return item['firstName'].toLowerCase().includes(search.toLowerCase())
    })

  }

  render() {
    const pageSize = 50
    if (!this.state.isModeSelected) {
      return (
        <div className="container">
          <ModeSelector onSelect={this.modeSelectHandler} />
        </div>
      )
    }

    const filteredData = this.getFilteredData()

    const displayData = _.chunk(this.state.data, pageSize)[this.state.currentPage]

    return (
      <div className="container">
        {
        this.state.isLoading
          ? <Loader />
          : <React.Fragment>
            <Search onSearch={this.searchHandler}/> 
             <Table
            data={displayData}
            onSort={this.onSort}
            sort={this.state.sort}
            sortField={this.state.sortField}
            onRowSelect={this.onRowSelect}
          />
          </React.Fragment>

        }

        {

        this.state.data.length > pageSize
        ?   <ReactPaginate
        previousLabel={'<'}
        nextLabel={'>'}
        breakLabel={'...'}
        breakClassName={'break-me'}
        pageCount={20}
        marginPagesDisplayed={2}
        pageRangeDisplayed={5}
        onPageChange={this.handlePageClick}
        containerClassName={'pagination'}
        activeClassName={'active'}
        pageClassName = "page-item"
        pageLinkClassName = "page-link"
        previousClassName= "page-item"
        nextClassName = "page-item"
        nextLinkClassName =  "page-link"
        previousLinkClassName =  "page-link"
        forcePage={this.state.currentPage}
         /> :null

        }


        {
          this.state.row
            ? <DetailRowView person={this.state.row} />
            : null
        }

      </div>
    );



  };

}
export default App;
